// src/context/AuthContext.js
import React, { createContext, useState, useEffect } from "react";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // null indicates not logged in

  // Here you can check if the user is authenticated (for example, using localStorage)
  useEffect(() => {
    const loggedUser = JSON.parse(localStorage.getItem("user")); // Replace with your logic
    if (loggedUser) {
      setUser(loggedUser);
    }
  }, []);

  const login = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData)); // Save user data
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user"); // Remove user data
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
