import React from "react";
import { Route, Routes } from "react-router-dom"; // Import Routes instead of Switch
import Home from "./components/Home";
import BlogPage from "./components/BlogPage";
import ReportIssue from "./components/ReportIssue";
import RealTimeTracking from "./components/RealTimeTracking";
import Login from "./components/Login";
import Register from "./components/Register";
import Polls from "./components/Polls";
import Navbar from "./components/Navbar";
import ProtectedRoute from "./components/ProtectedRoute"; // Import ProtectedRoute

function App() {
  return (
    <div>
      <Navbar />
      <Routes>
        <Route exact path="/" element={<Home />} />
        <Route path="/blogpage" element={<BlogPage />} />

        {/* Protect the routes that require authentication */}
        <ProtectedRoute path="/reportissue" element={<ReportIssue />} />
        <ProtectedRoute
          path="/realtimetracking"
          element={<RealTimeTracking />}
        />

        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <ProtectedRoute path="/polls" element={<Polls />} />
      </Routes>
    </div>
  );
}

export default App;

