import React, { createContext, useState, useEffect, useContext } from "react";

export const AuthContext = createContext();

// Create a custom hook to use the AuthContext
export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // null indicates not logged in

  // Check if the user is authenticated (for example, using localStorage)
  useEffect(() => {
    const loggedUser = JSON.parse(localStorage.getItem("user")); // Ensure "user" matches the key used in localStorage
    if (loggedUser) {
      setUser(loggedUser);
    }
  }, []);

  const login = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData)); // Save user data
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user"); // Remove user data
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
